mapper.select:
SELECT rfid_id as id,rfid_code as code,rfid_name as name, CASE rfid_status WHEN '1' THEN '运行中' WHEN '0' THEN '停用' WHEN '-1' THEN '故障' ELSE '其他' END as status ,location,lng,lat,addtime FROM cm_eplate_ydq WHERE 1=1
AND location LIKE '%${location}%'
AND rfid_name LIKE '%${name}%' ;

SELECT tw_id as id,tw_code as code,tw_name as name, CASE tw_status WHEN '1' THEN '运行中' WHEN '0' THEN '停用' WHEN '-1' THEN '故障' ELSE '其他' END as status, location,lng,lat,addtime FROM cm_eplate_tw WHERE 1=1
AND location LIKE '%${location}%'
AND tw_name LIKE '%${name}%';

SELECT COUNT(1) AS totalCount FROM cm_eplate_ydq WHERE 1=1

	 AND rfid_name = #{data.rfid_name}
	 AND location LIKE '%${data.location}%'
